od.reader.jsonp_batch_data(20160803, "feat", {
    "feat1370": "<h1 class=player>Accurate Magic Weapon</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Artificer<br><b>Benefit</b>: Whenever you use an artificer power that targets a weapon or an implement, the next attack roll with that weapon or implement before the end of the encounter gains a +1 bonus.<br></p><p class=publishedIn>Published in Eberron Player's Guide, page(s) 87.</p>",
    "feat2070": "<h1 class=player>Adamant Arrow Commander [Greater Style]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warlord, Adamant Arrow Student feat<br><b>Benefit</b>: When you are attacking with a greatbow or a longbow and you have proficiency with that weapon, you gain the following two benefits.<br>When you hit an enemy and at least one other enemy is providing it with cover from your attack, you deal damage equal to your Strength modifier to one enemy providing cover to it.<br>You can use a power associated with this feat as a ranged power instead of a melee power.<br></p><br>Associated Powers: Deadly Inspiration, Denying Smite, Inspiring War Cry, Sunder Armor, Warlord's Doom, Wounding Focus<br><p class=publishedIn>Published in Martial Power 2, page(s) 110.</p>",
    "feat750": "<h1 class=player>Aggressive Assault</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Rogue, Brutal Scoundrel class feature, First Strike class feature<br><b>Benefit</b>: At the beginning of an encounter, whenever you hit a target that has not yet acted, you slide that target 1 square.<br></p><p class=publishedIn>Published in Martial Power, page(s) 132.</p>",
    "feat2390": "<h1 class=player>Agile Running Attack</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Ranger, Running Attack class feature<br><b>Benefit</b>: When you move as part of using a ranger attack power, you ignore difficult terrain.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 131.</p>",
    "feat3450": "<h1 class=player>Ancient Lore of the Dawn War</h1><p class=flavor><b>Benefit</b>: You have studied the lore of old deities and the history of the Dawn War. You gain a +2 feat bonus to History and Religion checks, and you gain Supernal as a language.<br></p><p class=publishedIn>Published in Dragon Magazine 390.</p>",
    "feat3050": "<h1 class=player>Ancient Wisdom</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Wilden, shaman, voyage of the ancients power<br><b>Benefit</b>: When you use voyage of the ancients, the enemy you choose also grants combat advantage to each ally adjacent to your spirit companion until the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 13.</p>",
    "feat3630": "<h1 class=player>Archery Mastery</h1><p class=flavor><b>Prerequisite</b>: Any ranger at-will attack power that has a level<br><b>Benefit</b>: Choose one of your ranger at-will attack powers that has a level. You lose that power and gain one of the powers granted by the Expert Archer class feature.<br></p><p class=publishedIn>Published in Dragon Magazine 402.</p>",
    "feat3430": "<h1 class=player>Armored Sorcerer</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Sorcerer, proficiency with leather armor<br><b>Benefit</b>: When wearing leather armor, any resistance you gain from your Spell Source class feature increases by 2.<br></p><p class=publishedIn>Published in Dragon Magazine 390.</p>",
    "feat1490": "<h1 class=player>Astral Enmity</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Deva, avenger, oath of enmity power<br><b>Benefit</b>: If you use your memory of a thousand lifetimes racial power to increase an attack roll against your oath of enmity target, you deal extra damage equal to the bonus gained from the racial power.<br></p><p class=publishedIn>Published in Divine Power, page(s) 130.</p>",
    "feat2750": "<h1 class=player>Avandra's Boon</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any divine class, must worship Avandra<br><b>Benefit</b>: Whenever you roll a natural 1 on an attack roll or saving throw, you gain a +2 bonus to your next attack roll or saving throw made before the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 384, page(s) 70.</p>",
    "feat910": "<h1 class=player>Battle Acumen [Multiclass Fighter]</h1><p class=flavor><b>Prerequisite</b>: Any multiclass fighter feat, paragon multiclassing as a fighter<br><b>Benefit</b>: You gain the fighter's Combat Superiority class feature.<br></p><p class=publishedIn>Published in Martial Power, page(s) 149.</p>",
    "feat2690": "<h1 class=player>Beguiling Enchantment</h1><p class=flavor><b>Heroic Tier</b><br><b>Benefit</b>: Whenever you hit an enemy with a charm power, that enemy takes a -2 penalty to attack rolls against you until the end of its next turn.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 177.</p>",
    "feat1230": "<h1 class=player>Benighted Birthright</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Shadar-kai<br><b>Benefit</b>: You gain resist 5 necrotic and a +2 feat bonus to saving throws against ongoing necrotic damage.<br></p><p class=publishedIn>Published in Dragon Magazine Annual, Dragon Magazine 372, page(s) 9.</p>",
    "feat1310": "<h1 class=player>Black Hood Butcher [Greater Style]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Fighter, Black Hood Student feat<br><b>Benefit</b>: When you are attacking with a two-handed axe and you have proficiency with that weapon, you gain the following two benefits.<br>If you score a critical hit, each enemy adjacent to you takes damage equal to your Strength modifier.<br>You can use a power associated with this feat in place of a melee basic attack when making an attack as a result of your Combat Challenge class feature.<br></p><br>Associated Powers: Adamantine Strike, Crippling Smash, Crushing Blow, Exacting Strike, Griffon's Wrath<br><p class=publishedIn>Published in Martial Power 2, page(s) 112.</p>",
    "feat650": "<h1 class=player>Blade Initiate [Multiclass Swordmage]</h1><p class=flavor><b>Prerequisite</b>: Int 13<br><b>Benefit</b>: You gain training in the Arcana skill.<br>Once per day, when you are wielding a blade, you can invoke the Swordmage Warding class feature as a minor action. Until the end of the encounter, you gain a +1 bonus to AC (or a +3 bonus to AC if you have one hand free).<br>In addition, you can use swordmage implements.<br></p><br>Addition (2/10/2009)<br>At the end of the Benefit section, add “In addition, you can use swordmage implements.”<br><br>Addition (1/21/2009)<br>Add “as a minor action” to the end of the first sentence.<br><br><h1 class=dailypower>Swordmage Warding<span class=level>Feat Utility</span></h1><p class=powerstat><b>Daily</b><br><b>Minor Action</b>      <b>Personal</b> </p><p class=flavor><b>Effect:</b>Until the end of the encounter, while you are conscious and wielding either a light blade or a heavy blade, you gain a +1 bonus to AC, or a +3 bonus to AC if you are wielding a blade in one hand and have your other hand free.</p><p class=powerstat><b>Requirement</b>: You must have the Blade Initiate feat to use this feature.</p><br><p class=publishedIn>Published in Forgotten Realms Player's Guide, page(s) 139.</p>",
    "feat3390": "<h1 class=player>Blood's Passion [Multiclass Ardent]</h1><p class=flavor><b>Prerequisite</b>: Fervent Talent feat<br><b>Benefit</b>: Choose a power granted by an Ardent Mantle class feature. You can use that power once per day.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 141.</p>",
    "feat3770": "<h1 class=player>Chauntea's Comfort</h1><p class=flavor><b>Prerequisite</b>: Must worship Chauntea<br><b>Benefit</b>: When you use a power that allows an ally to spend a healing surge to regain hit points, you can lose a healing surge on that ally's behalf, so the ally benefits from the healing but does not spend a healing surge.<br></p><p class=publishedIn>Published in Dragon Magazine 412.</p>",
    "feat2150": "<h1 class=player>Clever Control</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Controller role<br><b>Benefit</b>: Whenever you target two or more creatures with a close or area attack power and you hit only one of those creatures, the attack deals 1d6 extra damage. The extra damage increases to 2d6 at 11th level and 3d6 at 21st level.<br></p><p class=publishedIn>Published in Dragon Magazine 382, page(s) 101.</p>",
    "feat2290": "<h1 class=player>Clever Tail</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Tiefling<br><b>Benefit</b>: Once per round, you can spend a free action to draw or stow an object weighing up to 1 pound with your tail. This feat does not give you the ability to make attacks with your tail.<br><br>You can use your tail to make Thievery checks when your hands are otherwise occupied. Your tail can even use thieves' tools.<br></p><p class=publishedIn>Published in Player's Handbook Races: Tiefling, page(s) 25.</p>",
    "feat1790": "<h1 class=player>Crimson Eye Action</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Human, assassin, assassin's shroud power<br><b>Benefit</b>: When you spend an action point, you also subject your assassin's shroud target to an additional shroud.<br></p><p class=publishedIn>Published in Dragon Magazine 379, page(s) 26.</p>",
    "feat1430": "<h1 class=player>Darkness Consumes [Divinity]</h1><p class=flavor><b>Prerequisite</b>: Channel Divinity class feature, must worship a deity of the darkness domain<br><b>Benefit</b>: You gain the Channel Divinity power darkness consumes.<br><br></p><br><h1 class=encounterpower>Darkness Consumes<span class=level>Feat Utility</span></h1><p class=flavor><i>You cause darkness to swirl around you and your allies for a moment.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b><br><b>Minor Action</b>      <b>Close</b> burst 1</p><p class=powerstat><b>Channel Divinity</b>: You can use only one channel divinity power per encounter</p><p class=powerstat><b>Target</b>: You and each ally in the burst</p><p class=flavor><b>Effect</b>: Each target gains concealment until the end of your next turn.</p><br><p class=publishedIn>Published in Divine Power, page(s) 110.</p>",
    "feat2590": "<h1 class=player>Deadly Draw</h1><p class=flavor><b>Heroic Tier</b><br><b>Benefit</b>: Whenever you pull or slide an enemy to a square adjacent to you, you gain combat advantage against that enemy until the end of your next turn.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 180.</p>",
    "feat3170": "<h1 class=player>Death's Grasp</h1><p class=flavor><b>Prerequisite</b>: Revenant, battlemind, mind spike power<br><b>Benefit</b>: You gain the following augmentation for your mind spike power.<br>    Augment 1: Each enemy adjacent to the target takes necrotic damage equal to your Constitution modifier.<br></p><p class=publishedIn>Published in Dragon Magazine 389.</p>",
    "feat3190": "<h1 class=player>Defiling Action</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Arcane defiling power<br><b>Benefit</b>: When you spend an action point to use an at-will or an encounter arcane attack power, you can use your arcane defiling with that power.<br></p><p class=publishedIn>Published in Dark Sun Campaign Setting, page(s) 105.</p>",
    "feat1510": "<h1 class=player>Demonbane Malediction</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Invoker, Covenant of Malediction class feature<br><b>Benefit</b>: If you hit a creature of the elemental origin with your maledictor's doom power, that target also grants combat advantage to the next ally of yours who attacks it before the end of your next turn.<br></p><p class=publishedIn>Published in Divine Power, page(s) 131.</p>",
    "feat670": "<h1 class=player>Disciple of Divine Wrath [Multiclass Avenger]</h1><p class=flavor><b>Prerequisite</b>: Wis 13<br><b>Benefit</b>: You gain training in Religion. Once per encounter, you can use the avenger's oath of enmity power. The effect lasts until the end of your next turn.<br>    In addition, you can wield avenger implements.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 196.</p>",
    "feat3130": "<h1 class=player>Disciple of Justice</h1><p class=flavor><b>Prerequisite</b>: Charisma 13<br><b>Benefit</b>: When you use your second wind, you can choose one ally adjacent to you to regain the hit points granted by it. If you do so, you don't regain the hit points.<br></p><p class=publishedIn>Published in Heroes of the Fallen Lands, page(s) 312, Heroes of the Forgotten Kingdoms, page(s) 313.</p>",
    "feat2410": "<h1 class=player>Draw First Blood</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any martial class<br><b>Benefit</b>: Your basic attacks against undamaged enemies deal extra damage equal to your Wisdom modifier.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 132.</p>",
    "feat3030": "<h1 class=player>Dwarven Rejuvenation</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Dwarf, artificer, Arcane Rejuvenation class feature<br><b>Benefit</b>: When an ally gains temporary hit points from Arcane Rejuvenation, that ally also gains a +2 bonus to all defenses until the start of his or her next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 33.</p>",
    "feat3490": "<h1 class=player>Dwindling Strength</h1><p class=flavor><b>Prerequisite</b>: Genasi, firedeath racial power<br><b>Benefit</b>: When you use firedeath, you are no longer grabbed and cannot be grabbed until the end of your next turn. In addition, enemies adjacent to you take a -5 penalty to damage rolls against you until the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 391.</p>",
    "feat1830": "<h1 class=player>Earthstrength Resilience</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warden, Earthstrength class feature<br><b>Benefit</b>: When you use your second wind, you gain resist 2 to all damage until the end of your next turn. The resistance increases to 5 at 11th level and 8 at 21st level.<br></p><p class=publishedIn>Published in Primal Power, page(s) 136.</p>",
    "feat1130": "<h1 class=player>Elemental Blade Attunement</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Genasi, swordmage<br><b>Benefit</b>: When you use a swordmage power that has the acid, cold, fire, lightning, or thunder keyword, you gain a +1 feat bonus to damage rolls.<br>This bonus increases to +2 at 11th level and to +3 at 21st level.<br></p><p class=publishedIn>Published in Arcane Power, page(s) 125.</p>",
    "feat3530": "<h1 class=player>Elemental Shield</h1><p class=flavor><b>Prerequisite</b>: Wilden, Nature's Aspect racial trait<br><b>Benefit</b>: Whenever you choose an aspect of nature to manifest, choose a damage type: cold, fire, lightning, or thunder. You gain resist 5 to that damage type for the next attack that hits you and deals that damage type. The resistance increases to 10 at 11th level and 15 at 21st level.<br></p><p class=publishedIn>Published in Dragon Magazine 393.</p>",
    "feat2030": "<h1 class=player>Eye Wand [Vistani Bloodline]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Wizard, Wand of Accuracy class feature, Vistani Heritage<br><b>Benefit</b>: If you miss with an attack roll modified by your Wand of Accuracy class feature, you can use evil eye of the Vistani, if available, as a free action against the target you missed.<br></p><p class=publishedIn>Published in Dragon Magazine 380, page(s) 85.</p>",
    "feat3650": "<h1 class=player>Fading Target</h1><p class=flavor><b>Prerequisite</b>: Gnome, fade away racial power<br><b>Benefit</b>: As an immediate reaction, you can use fade away when an enemy misses you with a melee attack.<br></p><p class=publishedIn>Published in Heroes of the Feywild, page(s) 128.</p>",
    "feat1330": "<h1 class=player>Fearsome Host [Tribal]</h1><p class=flavor><b>Benefit</b>: You gain a +2 feat bonus to Intimidate checks. This bonus increases by 1 for each ally within 10 squares of you who has this feat, to a maximum of +5.<br></p><p class=publishedIn>Published in Primal Power, page(s) 137.</p>",
    "feat1970": "<h1 class=player>Fighter Weapon Specialization</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Fighter, Fighter Weapon Talent class feature<br><b>Benefit</b>: You gain a +1 bonus to damage rolls when you hit with an attack using a weapon of the style you chose for the Fighter Weapon Talent class feature.<br></p><p class=publishedIn>Published in Dragon Magazine 379, page(s) 62.</p>",
    "feat1770": "<h1 class=player>Fortune's Predator</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Githzerai, druid, Primal Predator class feature<br><b>Benefit</b>: When you use wild shape to change from your beast form to your humanoid form, you can shift 2 squares instead of 1.<br></p><p class=publishedIn>Published in Dragon Magazine 378.</p>",
    "feat2570": "<h1 class=player>Frantic Recovery</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Human<br><b>Benefit</b>: When you spend an action point to gain an extra action, until the end of your turn, you can use your second wind as a minor action.<br></p><p class=publishedIn>Published in Dragon Magazine 383, page(s) 67.</p>",
    "feat2130": "<h1 class=player>Furious Demand</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: 2nd level, tiefling<br><b>Benefit</b>: You gain the furious demand feat power, which replaces a utility power of 2nd level or higher.<br>In addition, you gain a +2 feat bonus to Intimidate checks.<br></p><br><h1 class=dailypower>Furious Demand<span class=level>Feat Utility</span></h1><p class=flavor><i>Your soul burns with anger, causing your foe to quail in fear at the expression on your face.</i></p><p class=powerstat><b>Daily</b><br><b>Free Action</b>      <b>Personal</b> </p><p class=powerstat><b>Trigger</b>: You succeed on an Intimidate check or hit with an attack</p><p class=powerstat><b>Target</b>: The creature you intimidated or hit</p><p class=flavor><b>Effect</b>: You gain a +2 bonus to all Intimidate checks until the end of the encounter. In addition, the target takes a penalty to attack rolls against you equal to your Charisma modifier (save ends).</p><p class=powerstat>  <b>Aftereffect</b>: The target takes a penalty to attack rolls against you equal to your Charisma modifier until the end of its next turn.</p><br><p class=publishedIn>Published in Dragon Magazine 381, page(s) 87.</p>",
    "feat1270": "<h1 class=player>Furtive Signals [Guild]</h1><p class=flavor><b>Benefit</b>: You gain a +2 feat bonus to Bluff checks. You can communicate simple information silently with any ally who has this feat and has line of sight to your position. You can use Bluff to gain combat advantage or create a diversion to hide for one ally who has this feat and is within 5 squares of you, instead of for yourself. Your allies who have this feat can do the same for you.<br></p><p class=publishedIn>Published in Dragon Magazine 373, page(s) 11.</p>",
    "feat1290": "<h1 class=player>Garrote Expert [Multiclass]</h1><p class=flavor><b>Prerequisite</b>: 8th level, Garrote Training<br><b>Benefit</b>: You can swap one 6th-level or higher utility power you know for the body shield (garrote expert) utility power.<br></p><br><h1 class=encounterpower>Body Shield (Garrote Expert)<span class=level>Feat Utility</span></h1><p class=flavor><i>Your choking hold with the garrote allows you to manipulate your adversary into harm's way.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Weapon</b><br><b>Immediate Interrupt</b>      <b>Personal</b> </p><p class=flavor><b>Requirement</b>: You must be wielding a garrote.</p><p class=powerstat><b>Target</b>: You are grabbing a creature, and you are the target of a melee or ranged attack made by an enemy other than the creature you are grabbing.</p><p class=flavor><b>Effect</b>: The creature you are grabbing becomes the attack's target.</p><br><p class=publishedIn>Published in Dragon Magazine 373, page(s) 15.</p>",
    "feat610": "<h1 class=player>Gond's Craft [Divinity]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Channel Divinity class feature, must worship Gond<br><b>Benefit</b>: You can invoke the power of your deity to use Gond's craft.<br></p><br><h1 class=encounterpower>Gond's Craft<span class=level>Feat Utility</span></h1><p class=flavor><i>Gond inspires you with an uncanny understanding of the device or weapon in your hand.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b><br><b>Minor Action</b>      <b>Personal</b> </p><p class=powerstat><b>Channel Divinity</b>: You can use only one channel divinity power per encounter</p><p class=flavor><b>Effect</b>: You gain a +2 power bonus to any one attack roll made with a magic item power until the end of your next turn.</p><br><p class=publishedIn>Published in Forgotten Realms Player's Guide, page(s) 134.</p>",
    "feat1530": "<h1 class=player>Group Defense</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Half-elf, paladin<br><b>Benefit</b>: Allies gain a +1 bonus to all defenses against creatures marked by you.<br></p><p class=publishedIn>Published in Divine Power, page(s) 135.</p>",
    "feat3690": "<h1 class=player>Gruumsh's Battle Fury [Divinity]</h1><p class=flavor><b>Prerequisite</b>: Channel Divinity class feature, must worship Gruumsh<br><b>Benefit</b>: You gain the power Gruumsh's battle fury.<br></p><br><h1 class=encounterpower>Gruumsh's Battle Fury<span class=level>Feat Attack</span></h1><p class=flavor><i>An ally's violent success earns Gruumsh's reward. You channel divine power to inspire an ally to greater acts of violence.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Channel Divinity</b>, <b>Divine</b><br><b>Free Action</b>      <b>Close burst</b> 3</p><p class=powerstat><b>Trigger</b>: An ally within 3 squares of you drops a creature below 1 hit point.</p><p class=powerstat><b>Target</b>: The triggering ally in the burst</p><p class=flavor><b>Effect</b>: The target can charge or make a basic attack as a free action. In addition, the target grants combat advantage until the end of his or her next turn.</p><p class=powerstat><b>Special</b>: You can use only one channel divinity power per encounter.</p><br><p class=publishedIn>Published in The Book of Vile Darkness, page(s) 1-31.</p>",
    "feat3210": "<h1 class=player>Gulg Hunter Practice [Arena Fighting]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any martial class<br><b>Benefit</b>: You gain a +3 feat bonus to Athletics checks.<br>    When you use a power associated with this feat and hit an enemy with it while you have concealment, one ally you can see can shift 1 square as an opportunity action.<br></p><br>Associated Powers: Footwork Lure, Gloaming Cut, Intuitive Strike, Nimble Strike<br><p class=publishedIn>Published in Dark Sun Campaign Setting, page(s) 109.</p>",
    "feat1950": "<h1 class=player>Half-Orc Vengeance</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Half-orc, warden<br><b>Benefit</b>: The first time you are bloodied during an encounter, you gain a +2 feat bonus to attack rolls and damage rolls against creatures marked by you until the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 379, page(s) 45.</p>",
    "feat2990": "<h1 class=player>Hand of Fury</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Invoker, hand of radiance power<br><b>Benefit</b>: When you miss all targets with a daily invoker power, you can use hand of radiance as a minor action once before the end of your turn.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 41.</p>",
    "feat3290": "<h1 class=player>Harrying Step</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Battlemind, blurred step power, persistent harrier power<br><b>Benefit</b>: When you use your blurred step, you can teleport to any square adjacent to the triggering enemy instead of shifting.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 131.</p>",
    "feat290": "<h1 class=player>Healing Hands</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Paladin, lay on hands power<br><b>Benefit</b>: When you use the lay on hands power, the affected ally gains additional hit points equal to your Charisma modifier.<br></p><p class=publishedIn>Published in Player's Handbook, page(s) 195.</p>",
    "feat1070": "<h1 class=player>Hell's Implement [Multiclass]</h1><p class=flavor><b>Prerequisite</b>: Warlord, Pact Initiate (infernal pact)<br><b>Benefit</b>: You can use a polearm as a warlock implement.<br></p><br>Update (2/8/2010)<br>In the Benefit entry, replace the text with “You can use a polearm as a warlock implement.” This change synchronizes this feat with Player's Handbook 3 clarifications to implements and weapons.<br><br><p class=publishedIn>Published in Dragon Magazine 369, page(s) 55.</p>",
    "feat1630": "<h1 class=player>Hero of Faith [Multiclass Avenger]</h1><p class=flavor><b>Prerequisite</b>: Wis 15<br><b>Benefit</b>: You gain training in one skill from the avenger's class skills list.<br>Once per encounter, you can use the oath of enmity power. The effect lasts until you hit the target or until the target drops to 0 hit points. You cannot switch the target or extend the duration of the power in any way.<br>In addition, you can wield avenger implements.<br></p><br>Addition (10/14/2009)<br>Add the following sentence to the second paragraph, between the first and second sentences:<br>“The effect lasts until you hit the target or until the target drops to 0 hit points.”<br><br><p class=publishedIn>Published in Divine Power, page(s) 144.</p>",
    "feat1450": "<h1 class=player>Imminent Luck [Divinity]</h1><p class=flavor><b>Prerequisite</b>: Channel Divinity class feature, must worship a deity of the luck domain<br><b>Benefit</b>: You gain the Channel Divinity power imminent luck.<br><br></p><br><h1 class=encounterpower>Imminent Luck<span class=level>Feat Utility</span></h1><p class=flavor><i>Your god gives you another chance at good fortune.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b><br><b>Free Action</b>      <b>Personal</b> </p><p class=powerstat><b>Channel Divinity</b>: You can use only one channel divinity power per encounter</p><p class=powerstat><b>Trigger</b>: You use a power that gives you a reroll, and the reroll is lower than the original roll</p><p class=flavor><b>Effect</b>: You don't expend the use of the power that gave you the reroll.</p><br><p class=publishedIn>Published in Divine Power, page(s) 115.</p>",
    "feat2110": "<h1 class=player>Improved Augment Energy</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Artificer, Arcane Empowerment class feature<br><b>Benefit</b>: The Augment Energy option of your Arcane Empowerment class feature grants a +4 bonus to the attack roll instead of +2.<br></p><p class=publishedIn>Published in Dragon Magazine 381, page(s) 61.</p>",
    "feat2430": "<h1 class=player>Improved Insight</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warlord, Insightful Presence class feature<br><b>Benefit</b>: When an ally benefits from your Insightful Presence class feature, that ally can forgo the normal bonus to all defenses to instead gain a bonus to a single defense equal to your Charisma or Wisdom modifier until the end of your next turn.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 136.</p>",
    "feat3790": "<h1 class=player>Improved Lab Conditioning</h1><p class=flavor><b>Prerequisite</b>: Tinker gnome, lab conditioning racial power<br><b>Benefit</b>: When you use your lab conditioning racial power, you can also make a saving throw against one effect that a save can end.<br></p><p class=publishedIn>Published in Dragon Magazine 422.</p>",
    "feat790": "<h1 class=player>Improved Resources</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warlord, Resourceful Presence class feature<br><b>Benefit</b>: Add 2 to the damage bonus and the temporary hit points granted by your Resourceful Presence.<br></p><p class=publishedIn>Published in Martial Power, page(s) 136.</p>",
    "feat1010": "<h1 class=player>Improved Roar of Triumph</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Barbarian, Thaneborn Triumph class feature<br><b>Benefit</b>: When you use roar of triumph while raging, roar of triumph becomes a close burst 5 + your Charisma modifier, instead of a close burst 5, and you gain a +2 power bonus to weapon damage rolls until the end of your next turn.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 186.</p>",
    "feat2270": "<h1 class=player>Improved Speed of Thought</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Battlemind, speed of thought power<br><b>Benefit</b>: When you use your speed of thought, you can move 2 additional squares.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 181.</p>",
    "feat3470": "<h1 class=player>Knock Back</h1><p class=flavor><b>Prerequisite</b>: Dwarf<br><b>Benefit</b>: When you succeed on a saving throw to avoid being knocked prone, you can knock prone one enemy of size Large or smaller that is adjacent to you.<br></p><p class=publishedIn>Published in Dragon Magazine 391.</p>",
    "feat2350": "<h1 class=player>Kulkor Battlearm Rattler [Greater Style]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Rogue, Kulkor Battlearm Student feat, Ruthless Ruffian class feature<br><b>Benefit</b>: When you are attacking with an axe, a hammer, or a mace that has the versatile property and you have proficiency with that weapon, you gain the following two benefits.<br>When you hit a creature with a martial encounter power, any ally gains a +2 feat bonus to AC while adjacent to you until the end of your next turn.<br>You can use a power associated with this feat in place of a melee basic attack when making an opportunity attack.</p><br>Associated Powers: Daunting Attack, Dismaying Slash, <br>Enforced Threat: (Only when used as a melee attack)Felling Gash, Stinging Squall, <br>Stupefying Violence: (Only when used as a melee attack)<p class=publishedIn>Published in Martial Power 2, page(s) 118.</p>",
    "feat150": "<h1 class=player>Linguist</h1><p class=flavor><b>Prerequisite</b>: Intelligence 13<br><b>Benefit</b>: Choose three languages. You can now speak, read, and write those languages fluently.<br><b>Special</b>: You can take this feat more than once. Each time you select this feat, choose three new languages.<br></p><br>Update (8/5/2010)<br>Feat updated to match Essentials data.<br><br><p class=publishedIn>Published in Player's Handbook, page(s) 198, Heroes of the Fallen Lands, page(s) 315, Heroes of the Forgotten Kingdoms, page(s) 316.</p>",
    "feat1390": "<h1 class=player>Mark of Healing [Dragonmark]</h1><p class=flavor><b>Heroic Tier</b><br><b>Benefit</b>: Whenever you use a healing power on an ally or use Heal to allow an ally to spend his or her second wind, that ally can also make a saving throw.<br>You can master and perform rituals in the restoration category and the Remove Affliction ritual as if you had the Ritual Caster feat.<br></p><p class=publishedIn>Published in Eberron Player's Guide, page(s) 91.</p>",
    "feat1850": "<h1 class=player>Markings of the Predator</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Goliath, any primal class<br><b>Benefit</b>: When you bloody a creature, you gain a +1 bonus to all defenses until the end of your next turn.<br></p><p class=publishedIn>Published in Primal Power, page(s) 140.</p>",
    "feat2230": "<h1 class=player>Maw of the Hungry Killer</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Gnoll, assassin, assassin's shroud power<br><b>Benefit</b>: When you reduce a creature that has at least one of your shrouds on it to 0 hit points or fewer, you gain temporary hit points equal to 5 + the number of shrouds on the target. If you invoked your shrouds on the attack that reduced the creature to 0 hit points, you can also teleport to a space adjacent to your nearest ally within 10 squares.<br></p><p class=publishedIn>Published in Dragon Magazine 382, page(s) 30.</p>",
    "feat1750": "<h1 class=player>Mettle Proven</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Paladin, divine mettle<br><b>Benefit</b>: When an ally succeeds on a saving throw granted by your use of divine mettle, the ally can spend a healing surge.<br></p><p class=publishedIn>Published in Dragon Magazine 378.</p>",
    "feat2370": "<h1 class=player>Mountain Thunder Rampart [Greater Style]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Fighter, Mountain Thunder Student feat<br><b>Benefit</b>: When you are attacking with a one-handed hammer, a one-handed flail, or a one-handed mace and you have proficiency with that weapon, you gain the following two benefits.<br>Whenever you hit an enemy with a martial power, that enemy takes a –2 penalty to saving throws until the end of your next turn against effects that daze or stun.<br>When you attack with a power associated with this feat, the power gains the invigorating keyword.</p><br>Associated Powers: Anvil of Doom, Crushing Blow, Exacting Strike, Indomitable Battle Strike, Reckless Strike, Skullcrusher<br><p class=publishedIn>Published in Martial Power 2, page(s) 123.</p>",
    "feat30": "<h1 class=player>Mounted Combat</h1><p class=flavor><b>Heroic Tier</b><br><b>Benefit</b>: While you are riding a creature, it does not take the normal -2 penalty to attack rolls that is imposed on mounts. In addition, the creature can make any Athletics, Acrobatics, Endurance, or Stealth checks using your skill check modifier (not including any temporary bonuses or penalties) in place of its own.<br></p><br>Update (4/29/2011)<br>Removed mount keyword power dependence on this feat, and clarified the language to comply with Rules Compendium. <br><br><p class=publishedIn>Published in Player's Handbook, page(s) 199.</p>",
    "feat3230": "<h1 class=player>Nibenay's Glory Practice [Arena Fighting]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any martial class<br><b>Benefit</b>: You gain a +2 feat bonus to Endurance checks.<br>    Once per encounter, when you use a power associated with this feat and hit an enemy with it, your next attack against that enemy before the end of your next turn gains combat advantage.<br></p><br>Update (6/28/2011)<br>Changed to match July update.<br><br><br>Associated Powers: Brash Assault, Deft Strike, Marauder's Rush, Reaping Strike<br><p class=publishedIn>Published in Dark Sun Campaign Setting, page(s) 111.</p>",
    "feat2450": "<h1 class=player>Nimble Dodge</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Halfling, any martial class<br><b>Benefit</b>: When you make an enemy reroll an attack roll using your second chance racial power, the enemy takes a -5 penalty to the second attack roll and can't score a critical hit on that roll.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 138.</p>",
    "feat2710": "<h1 class=player>Ninth Legion Commander [Greater Style]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warlord, Ninth Legion Student feat<br><b>Benefit</b>: When you are attacking with a one-handed pick or a one-handed spear and you have proficiency with that weapon, you gain the following two benefits.<br>Allies adjacent to you gain a +2 feat bonus to AC and Reflex against attacks from enemies adjacent to you.<br>You can use a power associated with this feat in place of a melee basic attack when making an opportunity attack.<br></p><br>Associated Powers: Blood Begets Blood, Bolstering Blow, Girding Strike, Sacrificial Lure, Warlord's Doom, Warlord's Strike<br><p class=publishedIn>Published in Martial Power 2, page(s) 125.</p>",
    "feat2810": "<h1 class=player>Opportune Chaos Burst</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: Sorcerer, Wild Magic class feature<br><b>Benefit</b>: When you make your first attack roll each turn, choose odd numbers or even numbers. If you chose odd and the result on the die is an odd number, your Chaos Burst class feature allows you to roll two saving throws against effects that a save can end. If you chose even and the result on the die is an even number, your Chaos Burst class feature grants you a +3 bonus to AC until the start of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 385, page(s) 47.</p>",
    "feat2010": "<h1 class=player>Pervasive Gloom</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Shadar-kai, any divine class, must worship the Raven Queen<br><b>Benefit</b>: You gain the Channel Divinity power pervasive gloom.<br></p><br><h1 class=encounterpower>Pervasive Gloom<span class=level>Feat Utility</span></h1><p class=flavor><i>Night falls where you stand.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b>, <b>Zone</b><br><b>Free Action</b>      <b>Close</b> burst 1</p><p class=powerstat><b>Channel Divinity</b>: You can use only one Channel Divinity power per encounter.</p><p class=powerstat><b>Trigger</b>: You hit an enemy with an attack</p><p class=flavor><b>Effect</b>: The burst creates a zone of shadows that lasts until the start of your next turn. Squares within the zone are heavily obscured.</p><br><p class=publishedIn>Published in Dragon Magazine 380, page(s) 64.</p>",
    "feat3610": "<h1 class=player>Pick Strike</h1><p class=flavor><b>Prerequisite</b>: Power strike power<br><b>Benefit</b>: When you use power strike with a pick, you deal the maximum damage you could roll for power strike if your attack roll against the target was 18-20.<br></p><p class=publishedIn>Published in Mordenkainen's Magnificent Emporium, page(s) 24.</p>",
    "feat2850": "<h1 class=player>Plaguing Bloodhunt</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Tiefling, Bloodhunt racial trait, you have a spellscar<br><b>Benefit</b>: When you hit a bloodied enemy, it takes 3 extra necrotic damage. Increase this damage to 6 at 11th level, and 9 at 21st level. If you have the Student of the Plague feat, the extra damage becomes fire and necrotic damage.<br></p><p class=publishedIn>Published in Dragon Magazine 385, page(s) 25.</p>",
    "feat2610": "<h1 class=player>Power Throw</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Str 15<br><b>Benefit</b>: When you make a ranged attack with a heavy thrown weapon, you can take a -2 penalty to the attack roll. If the attack hits, you gain a +2 bonus to the damage roll. The bonus increases to +4 at 11th level and +6 at 21st level.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 182.</p>",
    "feat2050": "<h1 class=player>Practiced Study</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any martial class<br><b>Benefit</b>: You can master and perform martial practices. You learn one martial practice of your level or lower.<br></p><p class=publishedIn>Published in Dragon Magazine 381, page(s) 119, Martial Power 2, page(s) 138.</p>",
    "feat1550": "<h1 class=player>Protector's Commitment</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Cha 15, paladin<br><b>Benefit</b>: When you or your target is adjacent to a bloodied ally, you gain a +1 bonus to attack rolls.<br></p><p class=publishedIn>Published in Divine Power, page(s) 136.</p>",
    "feat3310": "<h1 class=player>Psionic Initiative [Augment]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Psionic Augmentation class feature<br><b>Benefit</b>: You gain a +3 feat bonus to initiative checks.<br>    When you roll initiative, you can spend 1 power point to increase the bonus to +6.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 133.</p>",
    "feat1090": "<h1 class=player>Purifying Light</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Cha 15, any divine class<br><b>Benefit</b>: Your radiant prayers gain a +1 bonus to attack rolls when used against undead targets. At 11th level, this bonus increases to +2. At 21st level, this bonus increases to +3.<br></p><p class=publishedIn>Published in Dragon Magazine 371, page(s) 8.</p>",
    "feat1350": "<h1 class=player>Radiant Recovery [Deva Bloodline]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Con 13, deva or Deva Heritage<br><b>Benefit</b>: When you hit with or are damaged by an attack that has the radiant keyword, you gain a number of temporary hit points equal to your Constitution modifier.<br></p><p class=publishedIn>Published in Dragon Magazine 374, page(s) 50.</p>",
    "feat810": "<h1 class=player>Rattling Wrath</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Cha 15, tiefling, rogue<br><b>Benefit</b>: When you use your infernal wrath racial power, any rattling powers you use against the target before the end of your next turn apply a -4 penalty to the target's attack rolls instead of a -2 penalty.<br></p><br>Update (4/9/2010)<br>Replace the Benefit entry with the following text: “When you use your infernal wrath racial power, any rattling powers you use against the target before the end of your next turn apply a –4 penalty to the target's attack rolls instead of a –2 penalty.”<br><br><p class=publishedIn>Published in Martial Power, page(s) 138.</p>",
    "feat370": "<h1 class=player>Reckless Scramble</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Rogue, Artful Dodger class feature<br><b>Benefit</b>: When a power lets you shift, you can instead choose to move that distance + 2 squares.<br></p><p class=publishedIn>Published in Martial Power, page(s) 138.</p>",
    "feat3710": "<h1 class=player>Restoration Mastery</h1><p class=flavor><b>Prerequisite</b>: Ritual Caster feat, mastery of Comrades' Succor and one other restoration ritual<br><b>Benefit</b>: When you succeed on a Heal check to grant second wind, the subject gains the defense bonus that second wind normally grants. If you succeed on a Heal check to grant a saving throw, the target gains a +2 power bonus to that saving throw. You can also master and perform restoration rituals as if your level were two higher.<br></p><p class=publishedIn>Published in Dragon Magazine 405.</p>",
    "feat2970": "<h1 class=player>Scion of Onat</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Ranger, Beast Mastery class feature<br><b>Benefit</b>: You gain training in Streetwise.<br>In addition, whenever you spend a healing surge while your beast companion is adjacent to you, it regains hit points equal to one-half your healing surge value, in addition to the normal benefit of spending a healing surge.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 45.</p>",
    "feat3090": "<h1 class=player>Selfless Protector</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Paladin<br><b>Benefit</b>: When an ally adjacent to you becomes bloodied or drops to 0 or fewer hit points, you can slide the ally 1 square and shift 1 square into the ally's vacated square as a free action.<br></p><p class=publishedIn>Published in Dragon Magazine 388.</p>",
    "feat3570": "<h1 class=player>Shadow Control</h1><p class=flavor><b>Prerequisite</b>: Born of Shadow feat<br><b>Benefit</b>: You gain a +1 bonus to all defenses while you are in dim light or darkness.<br></p><p class=publishedIn>Published in Heroes of Shadow, page(s) 158.</p>",
    "feat2830": "<h1 class=player>Shadowforged Killer</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Warforged, assassin, warforged resolve power, shade form power<br><b>Benefit</b>: You can expend your warforged resolve power as a free action to regain the use of your shade form power.<br></p><p class=publishedIn>Published in Dragon Magazine 385, page(s) 35.</p>",
    "feat950": "<h1 class=player>Sickening Finish</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Trained in Intimidate<br><b>Benefit</b>: Whenever you reduce an enemy to 0 hit points, you gain a +1 feat bonus to all defenses until the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine Annual, Dragon Magazine 368, page(s) 58.</p>",
    "feat2890": "<h1 class=player>Skilled Feint</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Rogue, trained in Bluff<br><b>Benefit</b>: You gain a +2 bonus to Bluff checks made to feint. In addition, when you make a Bluff check to cause an enemy to grant combat advantage to you and succeed, your Sneak Attack deals an extra die of damage to that enemy until the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 386, page(s) 17.</p>",
    "feat1470": "<h1 class=player>Solar Enemy [Divinity]</h1><p class=flavor><b>Prerequisite</b>: Channel Divinity class feature, must worship a deity of the sun domain<br><b>Benefit</b>: You gain the Channel Divinity power solar enemy.<br><br></p><br>Update (11/23/2009)<br>In the feat's power, add “until the end of your next turn” to the Effect line.<br><br><h1 class=encounterpower>Solar Enemy<span class=level>Feat Utility</span></h1><p class=flavor><i>The divine sunlight playing across your enemies is a sign of the radiance to come.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b><br><b>Minor Action</b>      <b>Close</b> burst 2</p><p class=powerstat><b>Channel Divinity</b>: You can use only one channel divinity power per encounter</p><p class=powerstat><b>Target</b>: Each enemy in the burst</p><p class=flavor><b>Effect</b>: Each target gains vulnerable 5 radiant, or its vulnerability to radiant damage increases by 5 until the end of your next turn.</p><br>Update (11/23/2009)<br>In the feat's power, add “until the end of your next turn” to the Effect line.<br><br><p class=publishedIn>Published in Divine Power, page(s) 120.</p>",
    "feat3150": "<h1 class=player>Spear Expertise</h1><p class=flavor><b>Benefit</b>: You gain a +1 feat bonus to weapon attack rolls that you make with a spear. When charging, you also gain a +1 bonus to damage rolls of weapon attacks that you make with a spear. Both of these bonuses increase to +2 at 11th level and +3 at 21st level.<br></p><p class=publishedIn>Published in Heroes of the Fallen Lands, page(s) 317, Heroes of the Forgotten Kingdoms, page(s) 318.</p>",
    "feat1670": "<h1 class=player>Spectral Reaping</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Revenant<br><b>Benefit</b>: You ignore insubstantial with all damage from an attack with which you deal the necrotic damage from your dark reaping racial power.<br></p><p class=publishedIn>Published in Dragon Magazine 376, page(s) 41.</p>",
    "feat1870": "<h1 class=player>Spirits of the Primal Dragons</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Dragonborn, any primal class<br><b>Benefit</b>: The first time you are bloodied during an encounter, your attacks deal 1d8 extra damage until the end of your next turn. This damage is the type you initially chose for your dragon breath racial power.<br></p><p class=publishedIn>Published in Primal Power, page(s) 142.</p>",
    "feat1030": "<h1 class=player>Stalker Spirit Adept</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Shaman, Stalker Spirit class feature<br><b>Benefit</b>: Any ally who starts his or her turn adjacent to your spirit companion can shift 1 square as a free action, as the first action during his or her turn.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 189.</p>",
    "feat3510": "<h1 class=player>Starshine Mantle</h1><p class=flavor><b>Prerequisite</b>: Fey Origin racial trait, Warden<br><b>Benefit</b>: When you use your second wind, you gain partial concealment until you make an attack or until the start of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 392.</p>",
    "feat470": "<h1 class=player>Strength of Valor</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Bard, Virtue of Valor class feature<br><b>Benefit</b>: When you grant an ally temporary hit points with your Virtue of Valor, that ally also gains a +2 bonus to the next damage roll he or she makes before the end of his or her next turn.<br></p><br>Revision (10/13/2009)<br>On the Benefit line, replace “your next turn” with “his or her next turn.”<br><br><p class=publishedIn>Published in Player's Handbook 2, page(s) 189.</p>",
    "feat1410": "<h1 class=player>Sturdy Shifter</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Shifter<br><b>Benefit</b>: Whenever you use your shifter racial power, you gain 5 temporary hit points. The temporary hit points increase to 10 at 11th level and 20 at 21st level.<br></p><p class=publishedIn>Published in Eberron Player's Guide, page(s) 93.</p>",
    "feat2870": "<h1 class=player>Symbol of the Sonnlinor</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Any divine class, must worship Moradin<br><b>Benefit</b>: You gain proficiency with all military hammers, axes, and picks, and you can use any hammer, axe, or pick you wield and are proficient with as an implement for any divine power you use.<br></p><p class=publishedIn>Published in Dragon Magazine 385, page(s) 76.</p>",
    "feat3070": "<h1 class=player>Sympathy of Flame</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Wizard<br><b>Benefit</b>: You gain a +2 feat bonus to damage rolls with arcane fire attacks against creatures taking ongoing fire damage. This bonus increases to +4 at 11th level and +6 at 21st level.<br></p><p class=publishedIn>Published in Dragon Magazine 388.</p>",
    "feat3670": "<h1 class=player>Teeny Target</h1><p class=flavor><b>Prerequisite</b>: Pixie<br><b>Benefit</b>: While you are in an ally's space, you have partial cover against melee attacks and ranged attacks.<br></p><p class=publishedIn>Published in Heroes of the Feywild, page(s) 130.</p>",
    "feat2470": "<h1 class=player>Telepathic Mark</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Kalashtar, fighter<br><b>Benefit</b>: Whenever you score a critical hit with a fighter attack power, you can mark each enemy within the range of your telepathy. The mark lasts until the end of your next turn.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 139.</p>",
    "feat3590": "<h1 class=player>Templar's Focus</h1><p class=flavor><b>Prerequisite</b>: 5th level, Templar's Domain feat<br><b>Benefit</b>: You gain the 5th level domain feature associated with the domain you chose for your Templar's Domain feat.<br></p><p class=publishedIn>Published in Class Compendium.</p>",
    "feat630": "<h1 class=player>Torm's Justice [Divinity]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Channel Divinity class feature, must worship Torm<br><b>Benefit</b>: You can invoke the power of your deity to use Torm's justice.<br></p><br><h1 class=encounterpower>Torm's Justice<span class=level>Feat Utility</span></h1><p class=flavor><i>The sight of evil fills Torm with a cold, clear wrath. When you invoke his name, his just anger becomes yours.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Divine</b><br><b>Minor Action</b>      <b>Personal</b> </p><p class=powerstat><b>Channel Divinity</b>: You can use only one channel divinity power per encounter</p><p class=flavor><b>Effect</b>: You gain a +2 power bonus to attack rolls against bloodied enemies until the end of your next turn.</p><br><p class=publishedIn>Published in Forgotten Realms Player's Guide, page(s) 137.</p>",
    "feat990": "<h1 class=player>Trickster's Blade Style [Arena Fighting]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Cha 13, any martial class<br><b>Benefit</b>: You gain a benefit with any of the following exploits you possess.<br></p><br>Associated Powers: <br>Deft Strike: If you score a critical hit with this exploit, you can shift 1 square after the attack.<br>Footwork Lure: You can shift 1 square before the attack.<br>Nimble Strike: If you shift after the attack, one ally adjacent to you can shift 1 square as a free action. The ally's shift must end in a square adjacent to the target.<br>Viper's Strike: If you have combat advantage against the target and hit with this exploit, one ally adjacent to you can shift 1 square.<p class=publishedIn>Published in Dragon Magazine Annual, Dragon Magazine 368, page(s) 62.</p>",
    "feat3550": "<h1 class=player>Vecna's Final Command [Divinity]</h1><p class=flavor><b>Prerequisite</b>: Any channel divinity power, must worship Vecna<br><b>Benefit</b>: You gain the Vecna's final command power.<br></p><br><h1 class=encounterpower>Vecna's Final Command<span class=level>Feat Utility</span></h1><p class=flavor><i>You hold death back to allow the dying one final attack.</i></p><p class=powerstat><b>Encounter</b> ✦     <b>Channel Divinity</b>, <b>Divine</b><br><b>Immediate Interrupt</b>      <b>Close</b> burst 5</p><p class=powerstat><b>Trigger</b>: A creature within 5 squares of you drops below 1 hit point.</p><p class=powerstat><b>Target</b>: The triggering creature in the burst</p><p class=flavor><b>Effect</b>: The target makes a basic attack as a free action against a creature you choose.</p><p class=powerstat><b>Special</b>: You can use only one channel divinity power per encounter.</p><br>Update (4/26/2012)<br>Changed in April 2012 updates.<br><br><p class=publishedIn>Published in Dragon Magazine 395, The Book of Vile Darkness, page(s) 1-32.</p>",
    "feat1810": "<h1 class=player>Venom Hand Killer</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Assassin<br><b>Benefit</b>: You gain a +2 feat bonus to poison damage rolls. The bonus increases to +3 at 11th level and +4 at 21st level.<br></p><p class=publishedIn>Published in Dragon Magazine 379, page(s) 27.</p>",
    "feat2630": "<h1 class=player>Warding Defense</h1><p class=flavor><b>Heroic Tier</b><br><b>Benefit</b>: Whenever you use the total defense action while wielding a shield, allies adjacent to you gain a +2 bonus to AC and Reflex until the start of your next turn or until they are no longer adjacent to you.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 184.</p>",
    "feat970": "<h1 class=player>Whip Specialist [Multiclass]</h1><p class=flavor><b>Prerequisite</b>: Whip Training, 10th level<br><b>Benefit</b>: You can swap one 9th-level or higher daily attack power you know for the entangling whip attack power.<br></p><br><h1 class=dailypower>Entangling Whip<span class=level>Feat Attack</span></h1><p class=flavor><i>Your whip is like an extension of you, coiling around your enemy so you can drag that foe wherever you like.</i></p><p class=powerstat><b>Daily</b> ✦     <b>Weapon</b><br><b>Standard Action</b>      <b>Melee</b> weapon</p><p class=flavor><b>Requirement</b>: You must be wielding a whip.</p><p class=powerstat><b>Target</b>: One creature</p><p class=powerstat><b>Attack</b>: Dexterity vs. Reflex</p><p class=flavor><b>Hit</b>: 2[W] + Dexterity modifier damage, the target is grabbed (until escape) and knocked prone. The target takes a –5 penalty to escape the grab, and while you have the target grabbed you can pull it 1 square as a minor action. While grabbing the target, you cannot make attacks with the whip. You can sustain the grab as long as the target is within 2 squares of you.<br>At 15th level, increase to 3[W] damage.<br>At 25th level, increase to 4[W] damage.</p><p class=powerstat><b>Miss</b>: The target is grabbed (until escape) and knocked prone. While you have the target grabbed you can pull it 1 square as a minor action. While grabbing the target, you cannot make attacks with the whip. You can sustain the grab as long as the target is within 2 squares of you.</p><br><p class=publishedIn>Published in Dragon Magazine 368, page(s) 67.</p>",
    "feat2550": "<h1 class=player>Wrathful Warrior [Multiclass Fighter]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: Str 13 or Con 13<br><b>Benefit</b>: You gain training in one skill from the fighter's class skills list.<br>Once per encounter, when you are hit by a melee attack or a close attack, you gain temporary hit points equal to your Constitution modifier.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 146.</p>",
    "feat3750": "<h1 class=player>Wrist Biter</h1><p class=flavor><b>Prerequisite</b>: Goblin race, goblin tactics racial power<br><b>Benefit</b>: When you use goblin tactics in response to an adjacent enemy's attack, the enemy takes 1d4 damage right before you shift. This damage increases to 2d4 at 11th level and 3d4 at 21st level.<br></p><p class=publishedIn>Published in Into the Unknown: The Dungeon Survival Handbook, page(s) 38.</p>",
    "feat50": "<h1 class=player>Agile Athlete </h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level<br><b>Benefit</b>: When you make an Acrobatics check or an Athletics check, roll twice and use the higher result.<br><br></p><p class=publishedIn>Published in Player's Handbook, page(s) 202.</p>",
    "feat830": "<h1 class=player>Agile Tempest</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Dex 15, fighter, Tempest Technique class feature<br><b>Benefit</b>: The damage bonus granted by your Tempest Technique when you wear chainmail or light armor increases by 1. At 21st level, it increases by 2.<br></p><p class=publishedIn>Published in Martial Power, page(s) 140.</p>",
    "feat3330": "<h1 class=player>Anchoring Push</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, psion, forceful push power<br><b>Benefit</b>: When you use your forceful push, you also slow the target until the end of your next turn.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 135.</p>",
    "feat310": "<h1 class=player>Arcane Reach</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Dex 15<br><b>Benefit</b>: When using a close arcane attack power, you can choose a square within 2 squares of yours as the origin square. The power still follows the rules for close attacks.<br></p><p class=publishedIn>Published in Player's Handbook, page(s) 202.</p>",
    "feat1170": "<h1 class=player>Arcane Ritualist</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, any arcane class<br><b>Benefit</b>: Choose a ritual that you know. Change the key skill of that ritual to Arcana and gain a +2 bonus to skill checks made as part of that ritual.<br>Each time you learn a new ritual, you can change the ritual affected by this feat to the newly learned ritual.<br><b>Special</b>: You can select this feat more than once. Each time you select this feat, choose a different ritual.<br></p><p class=publishedIn>Published in Arcane Power, page(s) 130.</p>",
    "feat3250": "<h1 class=player>Arena Battle Rhythm</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Dex 13, fighter, Arena Training class feature<br><b>Benefit</b>: Whenever you make a melee attack with one of your arena weapons and deal no damage, the target of your attack takes damage equal to your Dexterity modifier.<br></p><p class=publishedIn>Published in Dark Sun Campaign Setting, page(s) 113.</p>",
    "feat1890": "<h1 class=player>Bolstering Guardian</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, druid, Primal Guardian class feature<br><b>Benefit</b>: When you reduce an enemy to 0 hit points with a druid attack power, one ally adjacent to you gains temporary hit points equal to your Constitution modifier.<br></p><p class=publishedIn>Published in Primal Power, page(s) 143.</p>",
    "feat2490": "<h1 class=player>Deft Blade</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, any martial class<br><b>Benefit</b>: When you make a basic attack using a light blade, you can target AC or Reflex.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 142.</p>",
    "feat1570": "<h1 class=player>Devout Guidance</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, avenger, divine guidance power<br><b>Benefit</b>: When you use your divine guidance, the target gains a +3 power bonus to the second attack roll.<br></p><p class=publishedIn>Published in Divine Power, page(s) 138.</p>",
    "feat2790": "<h1 class=player>Dual Mind Reserves</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, kalashtar, any psionic class, Psionic Augmentation class feature<br><b>Benefit</b>: When you use your second wind, you also regain 1 power point.<br></p><p class=publishedIn>Published in Dragon Magazine 385, page(s) 16.</p>",
    "feat2770": "<h1 class=player>Fey Revel Glamor</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, warlock, Warlock's Curse class feature, fey pact<br><b>Benefit</b>: Whenever you deal Warlock's Curse damage to a target and do so with a warlock daily attack power, the target is slowed (save ends). If the power already subjects the target to a slowing effect that a save can end, the target is instead immobilized (save ends).<br></p><p class=publishedIn>Published in Dragon Magazine 384, page(s) 31.</p>",
    "feat930": "<h1 class=player>Fierce Charge</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, gnoll, ferocious charge racial power<br><b>Benefit</b>: When you use your ferocious charge power, you can choose to make an at-will melee attack instead of a melee basic attack.<br></p><p class=publishedIn>Published in Dragon Magazine 367, page(s) 54.</p>",
    "feat1990": "<h1 class=player>Foul Torrent</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, genasi, causticsoul elemental manifestation, acid surge racial power<br><b>Benefit</b>: As part of your acid surge racial power, you can shift your speed instead of half your speed.<br></p><p class=publishedIn>Published in Dragon Magazine 380, page(s) 21.</p>",
    "feat850": "<h1 class=player>Group Flanking</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, half-elf, rogue<br><b>Benefit</b>: While you're flanking an enemy, all allies gain combat advantage against that enemy.<br></p><p class=publishedIn>Published in Martial Power, page(s) 143.</p>",
    "feat2650": "<h1 class=player>Healing Fragments</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, shardmind, Telepathy racial trait<br><b>Benefit</b>: Whenever you spend a healing surge, each ally within your telepathy range regains hit points equal to your Wisdom modifier.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 188.</p>",
    "feat3730": "<h1 class=player>Icy Heart</h1><p class=flavor><b>Prerequisite</b>: 11th level<br><b>Benefit</b>: You gain a +3 feat bonus to cold damage rolls. This bonus increases to +4 at 21st level.<br>    In addition, whenever you take cold damage from an attack, each enemy within 2 squares of you becomes slowed until the end of your next turn.<br></p><p class=publishedIn>Published in Heroes of the Elemental Chaos, page(s) 144.</p>",
    "feat3110": "<h1 class=player>Luck of the Gods</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level<br><b>Benefit</b>: When you roll a natural 20 on an attack roll, skill check, or saving throw, remove all effects on you that a save can end.<br></p><p class=publishedIn>Published in Dragon Magazine 388.</p>",
    "feat2310": "<h1 class=player>Mammon's Theft of Health</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, tiefling<br><b>Benefit</b>: When you use your second wind, you can choose for an ally within 5 squares of you to lose a healing surge. If you do, you regain extra hit points equal to your healing surge value.<br></p><p class=publishedIn>Published in Player's Handbook Races: Tiefling, page(s) 28.</p>",
    "feat3350": "<h1 class=player>Master's Instruction</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, monk<br><b>Benefit</b>: When you use a monk encounter attack power and miss at least one target, one ally you can see gains a +2 bonus to his or her next attack roll against one target you missed with the attack power before the end of your next turn.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 138.</p>",
    "feat530": "<h1 class=player>Paragon Defenses</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level<br><b>Benefit</b>: You gain a +1 feat bonus to Fortitude, Reflex, and Will.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 191.</p>",
    "feat2950": "<h1 class=player>Perfect Fracture</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, shardmind, shard swarm racial power<br><b>Benefit</b>: You can use your shard swarm racial power as an immediate reaction when you become bloodied.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 17.</p>",
    "feat2510": "<h1 class=player>Poisonous Beast</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, ranger, Beast Mastery class feature (serpent or spider)<br><b>Benefit</b>: Whenever your beast companion hits a creature with a melee basic attack, that creature takes ongoing 5 poison damage (save ends).<br>This ongoing damage increases to 10 at 21st level.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 143.</p>",
    "feat2090": "<h1 class=player>Prisoner's Resolve</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, warlock, infernal pact<br><b>Benefit</b>: If you fail a saving throw, you can take 10 damage to make another saving throw against the same effect. You can do this only once per effect per round. This damage cannot be reduced in any way.<br></p><p class=publishedIn>Published in Dragon Magazine 381, page(s) 53.</p>",
    "feat1690": "<h1 class=player>Reaping Burst</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Con 13, revenant<br><b>Benefit</b>: When you use your dark reaping racial power, instead of its normal effect you can deal extra necrotic damage equal to your Constitution modifier to all targets you hit with a burst or blast power that you use before the end of your next turn.<br></p><p class=publishedIn>Published in Dragon Magazine 376, page(s) 42.</p>",
    "feat3410": "<h1 class=player>Returning Resilience [Elan Bloodline]</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Elan Heritage<br><b>Benefit</b>: When you use your second wind, you regain the use of your elan resilience power.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 144.</p>",
    "feat1910": "<h1 class=player>Thunderous Cry</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, barbarian, war cry power<br><b>Benefit</b>: When you use war cry, the targets grant combat advantage to you until the end of your next turn.<br></p><p class=publishedIn>Published in Primal Power, page(s) 145.</p>",
    "feat1590": "<h1 class=player>Touch of Salvation</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Cha 15, paladin, lay on hands or virtue’s touch power<br><b>Benefit</b>: When you use your lay on hands or virtue’s touch on an ally, that ally can make a saving throw with a bonus equal to your Charisma modifier.<br></p><p class=publishedIn>Published in Divine Power, page(s) 140.</p>",
    "feat2250": "<h1 class=player>Venom Master</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, drow<br><b>Benefit</b>: When you hit a creature with your darkfire racial power, they gain vulnerability 5 poison until the end of the encounter.<br></p><br>Revision (2/24/2010)<br>Change benefit to read “When you hit a creature with your darkfire racial power they gains vulnerability 5 poison until the end of the encounter.”<br><br><p class=publishedIn>Published in Dragon Magazine 382, page(s) 67.</p>",
    "feat2190": "<h1 class=player>Venomous Fang Form</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, druid, wild shape power<br><b>Benefit</b>: While you are in beast form, all your melee attacks with the poison keyword ignore the first 10 points of the target's poison resist. This increases to 15 points at 21st level.<br></p><p class=publishedIn>Published in Dragon Magazine 382, page(s) 54.</p>",
    "feat1190": "<h1 class=player>Vestige Versatility</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, warlock, Vestige Pact class feature<br><b>Benefit</b>: Whenever you are allowed to change your vestige, you can choose to have two active vestiges instead of one. When your pact boon is triggered or when you use your eyes of the vestige power, you choose which vestige's benefit applies.<br>If you would ordinarily be required to choose between the two primary vestiges, this feat enables you to have both of them as active vestiges. If you use a daily attack power that gives you access to a new vestige, you can choose for that daily power vestige to replace either of your two active vestiges.<br></p><p class=publishedIn>Published in Arcane Power, page(s) 133.</p>",
    "feat1710": "<h1 class=player>Vigorous Familiar [Familiar]</h1><p class=flavor><b>Heroic Tier</b><br><b>Prerequisite</b>: 11th level, artificer, Arcane Familiar feat, Mark of Handling feat or member of House Vadalis<br><b>Benefit</b>: Your knowledge of House Vadalis's beast breeding techniques allows you to form your familiar with greater speed and resilience. Your familiar gains a +2 feat bonus to speed and a +1 feat bonus to AC.<br></p><p class=publishedIn>Published in Dragon Magazine 377, page(s) 35.</p>",
    "feat870": "<h1 class=player>Warlord's Formation</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, Int 17, warlord<br><b>Benefit</b>: When you use the aid another, aid attack, or aid defense action, you can target up to two allies with that action.<br></p><br>Update (9/10/2010)<br>In the Benefit entry, replace the first sentence with the following sentence: “When you use the aid another, aid attack, or aid defense action, you can target up to two allies with that action.” Delete the second sentence.<br><br><p class=publishedIn>Published in Martial Power, page(s) 145.</p>",
    "feat1050": "<h1 class=player>Wild Spellfury</h1><p class=flavor><b>Paragon Tier</b><br><b>Prerequisite</b>: 11th level, sorcerer, Wild Magic class feature<br><b>Benefit</b>: Whenever you hit an enemy with a sorcerer at-will attack power, each enemy adjacent to you takes damage equal to your Dexterity modifier.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 193.</p>",
    "feat110": "<h1 class=player>Arcane Mastery</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, Wizard<br><b>Benefit</b>: Once per encounter, you can spend an action point to regain the use of a daily wizard power you've already used today, instead of taking an extra action.<br></p><p class=publishedIn>Published in Player's Handbook, page(s) 206.</p>",
    "feat3370": "<h1 class=player>Battlemind Menace</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, battlemind, blurred step power<br><b>Benefit</b>: When you use your blurred step, you gain a +2 power bonus to all defenses until the start of your next turn.<br></p><p class=publishedIn>Published in Psionic Power, page(s) 139.</p>",
    "feat250": "<h1 class=player>Bludgeon Mastery</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, Str 19, Con 19<br><b>Benefit</b>: When you make a melee weapon attack with a hammer, a mace, or a staff, you can score a critical hit on a natural roll of 19 or 20.<br></p><br>Addition (7/28/2008)<br>Add \"a \" before words \"mace\" and \"staff\" in Benefits line.<br><br><p class=publishedIn>Published in Player's Handbook, page(s) 206.</p>",
    "feat350": "<h1 class=player>Bow Mastery</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level<br><b>Benefit</b>: When you make a ranged attack with a bow or a crossbow, you can score a critical hit on a roll of 19–20.<br></p><p class=publishedIn>Published in Player's Handbook 2, page(s) 194.</p>",
    "feat2670": "<h1 class=player>Burden of Vision</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, wilden, Nature's Aspect racial trait<br><b>Benefit</b>: Whenever you use a wilden racial power, you gain blindsight 10 until the end of your next turn.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 190.</p>",
    "feat3270": "<h1 class=player>Defiler's Rejuvenation</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, arcane defiling power, 1 or more power points<br><b>Benefit</b>: When your arcane defiling deals damage to one or more of your allies, you regain 2 power points.<br></p><p class=publishedIn>Published in Dark Sun Campaign Setting, page(s) 115.</p>",
    "feat1250": "<h1 class=player>Enmity's Dark Vigor</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, shadar-kai, avenger class<br><b>Benefit</b>: When your oath of enmity target hits you, you gain temporary hit points equal to your Wisdom modifier.<br></p><p class=publishedIn>Published in Dragon Magazine Annual, Dragon Magazine 372, page(s) 10.</p>",
    "feat330": "<h1 class=player>Font of Radiance</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level<br><b>Benefit</b>: When you score a critical hit with a power that has the radiant keyword, the target begins to glow brightly (save ends).<br>    The target's space and all squares adjacent to it are illuminated by bright light. Invisible creatures become visible while they are in affected squares, and attack rolls against creatures in those squares take no penalty for concealment. Any foe that ends its turn in an affected square (including the original target) takes 3d6 radiant damage.<br></p><p class=publishedIn>Published in Player's Handbook, page(s) 207.</p>",
    "feat2170": "<h1 class=player>Halo of Tethering</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, avenger, oath of enmity power<br><b>Benefit</b>: When an oath of enmity target that is adjacent to you shifts, you can shift 1 square into a square it vacated.<br></p><p class=publishedIn>Published in Dragon Magazine 382, page(s) 41.</p>",
    "feat3010": "<h1 class=player>Inexorable Tide</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, invoker, maledictor's doom power<br><b>Benefit</b>: Whenever a target subject to a penalty to attack rolls from your maledictor's doom power takes damage from any attack, you can push the target 1 square as a free action.<br></p><p class=publishedIn>Published in Dragon Magazine 387, page(s) 43.</p>",
    "feat1730": "<h1 class=player>Limning Challenge</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, paladin<br><b>Benefit</b>: Your attacks against targets marked by you with divine challenge or your divine sanction ignore penalties from concealment or total concealment.<br></p><p class=publishedIn>Published in Dragon Magazine 377, page(s) 45.</p>",
    "feat2530": "<h1 class=player>Lingering Illusion</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, gnome, rogue, Sneak Attack class feature<br><b>Benefit</b>: You gain concealment until the end of the encounter against any enemy you deal Sneak Attack damage to while you are invisible.<br></p><p class=publishedIn>Published in Martial Power 2, page(s) 144.</p>",
    "feat890": "<h1 class=player>Mobile Warrior</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, Dex 17, fighter<br><b>Benefit</b>: When you make an attack with a flail, a heavy blade, a light blade, or a spear, you can shift 1 square after resolving the attack, whether or not it hits.<br></p><p class=publishedIn>Published in Martial Power, page(s) 147.</p>",
    "feat1610": "<h1 class=player>Pious Champion</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, paladin, virtue’s touch or lay on hands power<br><b>Benefit</b>: When you use your virtue’s touch or lay on hands, you can target one or two creatures. If you target two creatures with lay on hands, you spend one healing surge and each target regains hit points as normal for the power.<br></p><p class=publishedIn>Published in Divine Power, page(s) 142.</p>",
    "feat1110": "<h1 class=player>Scent of Blood [Vampire Bloodline]</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, Vampire Bloodline feat<br><b>Benefit</b>: Living, bloodied enemies grant you combat advantage.<br></p><p class=publishedIn>Published in Dragon Magazine Annual, Dragon Magazine 371, page(s) 35.</p>",
    "feat1930": "<h1 class=player>Stone Heart Spirit</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, shaman, World Speaker Spirit class feature<br><b>Benefit</b>: Your spirit companion can provide cover to you and your allies.<br></p><p class=publishedIn>Published in Primal Power, page(s) 148.</p>",
    "feat1210": "<h1 class=player>Trusted Spellcasting</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, any arcane class<br><b>Benefit</b>: Choose one arcane encounter attack power that you know and that does not already deal damage on a miss. That power now deals half damage on a miss.<br></p><p class=publishedIn>Published in Arcane Power, page(s) 135.</p>",
    "feat2730": "<h1 class=player>Vicious Stomp</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, minotaur, goring charge racial power<br><b>Benefit</b>: When you knock a target prone with your goring charge, you can make a melee basic attack against the target as a free action.<br></p><p class=publishedIn>Published in Player's Handbook 3, page(s) 193.</p>",
    "feat550": "<h1 class=player>Warding Curse</h1><p class=flavor><b>Epic Tier</b><br><b>Prerequisite</b>: 21st level, warlock, Warlock's Curse class feature<br><b>Benefit</b>: You gain a +2 bonus to all defenses against any creature currently affected by your Warlock's Curse.<br></p><p class=publishedIn>Published in Arcane Power, page(s) 135.</p>"
})